mapper.select:
SELECT g.joint_shopping_group_num, g.joint_shopping_group_name, g.joint_shopping_products, g.joint_shopping_products_state, g.joint_shopping_products_file_directory, g.joint_shopping_info, g.joint_shopping_cost, g.joint_shopping_group_maximum_count, g.joint_shopping_participation_maximum_count, g.host_invoice_num, c.joint_shopping_category_num as "category.joint_shopping_category_num", c.joint_shopping_category_title as "category.joint_shopping_category_title", g.user_id FROM joint_shopping_group g JOIN joint_shopping_category c ON (g.joint_shopping_category_num = b.joint_shopping_category_num) WHERE g.joint_shopping_group_state = 'A' OR g.joint_shopping_group_state = 'C'
AND g.category_num = #{categoryNum}
AND g.group_name LIKE CONCAT('%', #{groupName}, '%')
ORDER BY g.joint_shopping_group_num DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM joint_shopping_group WHERE joint_shopping_group_state = 'A' OR joint_shopping_group_state = 'C'
AND g.category_num = #{categoryNum}
AND g.group_name LIKE CONCAT('%', #{groupName}, '%')
